function f = differentpowers(x)
% DIFFERENTPOWERS Step shifted different powers function
% Properties: Due to the different exponents the sensitivity relation of
% the xi-variables continuously worsens when approaching the optimum.
% 
% * x_even variables are rounded to integers.
%
M = numel(x);
evenidx = 2 : 2 : M;
x(evenidx) = round(x(evenidx));
x = reshape(x, 1, M);
xopt = 20 * [2.12547870316477,-3.32935885368953,-2.51060344241750,3.34168494889769,3.13094468673168,-0.338545392377010,0.479773652165919,1.83412894292492,-0.0694333987526079,2.08661343223780,2.03609871475553,-0.622028919392831,-2.96689017937966,-0.766641102320289,3.03772444913925,3.46945307646410,-3.72529135970631,-0.685809148961574,3.93503470396994,0.501078596623684,-2.52354193628404,-0.785456447247210,-4.63343349252382,2.23982697271643,0.332131361809374,-1.86613651202045,2.91082347643636,-1.62965401507468,3.33250701274107,1.81538633505963,-3.40874904069388,-3.39490254782126,-0.177078015914434,1.40849786758389,0.103340322003012,-1.73698203171339,-3.27194515819713,1.52647342615691,2.24551688849225,1.67333089821988,0.832745274088299,-2.36867853913546,-3.27663182376649,-0.00715114688301100,-4.95499409806381,2.45195543522427,-3.49780801603292,0.124880704119777,4.16896485826329,0.190783006368594,1.36896546891745,-2.03191818752118,4.36953879837086,-4.40722799997048,-0.744686578646174,4.51258553438657,-1.19836859101275,-2.15680656502926,-2.31987415871267,-4.33795542108918,1.41589568534110,2.25935934433305,2.15401017632772,3.70009950195522,-1.57291683095151,4.73499974477293,3.59249119949335,-3.13448492076592,-0.511688324374523,-4.75164773618378,-0.679842672982072,-2.12723978917976,-2.97622758579252,-4.46656711246970,-1.87121032267288,-2.74762415712952,-0.785279111196352,-2.54749482700188,2.67205508763707,-1.42110940452011,-1.26400415174635,2.02812517737639,-1.93082062442330,-1.82135741872536,-1.41156348394627,-4.15721234909976,3.61704275568994,-3.96293089614700,-0.244650216689441,-4.07783826075685,2.26578656527873,-4.33677440202880,-4.95661977266845,-1.19749522072762,-3.69043203163983,1.65251236382605,-0.824216687558831,-3.10767712477534,-2.31297733702409,-3.10223400585407];
x = x - xopt(1:M);
f = sqrt(sum(abs(x).^(2+4/(M-1)*(0:M-1))));
end

